module Katas.Faberge where
import Kyu3.Faberge
import Test.Hspec

main :: IO ()
main = hspec spec

spec :: Spec
spec = describe "Faberge" $ do
    it "should work for some basic tests" $ do
      heigth 0 14 `shouldBe` 0
      heigth 2 0  `shouldBe` 0
      heigth 2 14 `shouldBe` 105
      heigth 7 20 `shouldBe` 137979
      
    it "should work for some advanced tests" $ do
      heigth 7   500 `shouldBe` 1507386560013475
      heigth 237 500 `shouldBe` 431322842186730691997112653891062105065260343258332219390917925258990318721206767477889789852729810256244129132212314387344900067338552484172804802659
      heigth 477 500 `shouldBe` 3273390607896141870013189696827599152216642046043064789483291368096133796404674554883270092325904157150886684127420959866658939578436425342102468327399
      
    it "should work for some serious tests :)" $ do
      heigth 477  10000 `shouldBe` 
      heigth 4477 10000 `shouldBe` 
      heigth 9477 10000 `shouldBe` 
      
